import React from "react";
import StripeCheckout from "react-stripe-checkout";
import Axios from "axios";

const StripeCheckoutButton=({price})=>{
    const priceForStripe=price*100;
    const publishableKey="pk_test_Go3fUtLSw5sbv0RpYoOobazg00QUmefR0Z";
    const onToken=token=>{
        Axios({url:"payment",method:"post",data:{
            amount:priceForStripe,
            token
        }}).then(respone=>{
            alert("Payment Successful!")
        }).catch(error=>{
            console.log("Payment error :",JSON.parse(error));
            alert()
        })
        alert("Payment Successful")
    }
    return(
        <StripeCheckout label="Pay now" name="Le Thang Ltd." billingAddress shippingAddress image="https://sendeyo.com/up/d/f3eb2117da" description={`Your total is ${price}$`} amount={priceForStripe} panelLabel="Pay Now" token={onToken} stripeKey={publishableKey}  />
    )
}
export default StripeCheckoutButton;
