import React from "react";

const FormInput=({handleChane,label,...otherprop})=>(
    <div className="group">
        <input className="form-input" onChange={handleChane} {...otherprop} />
        {
            label?(
            <label className={`${otherprop.value.length}?"shrink":""} form-input-label`}>{label}</label>
            ):null
        }
    </div>
)
export default FormInput;
