import React, { Component } from "react";
import CollectionOverview from "../CollectionOverview/CollectionOverview";
import { Route } from "react-router-dom";
import Collection from "../Collection/Collection";

import { connect } from "react-redux";
import { createStructuredSelector } from "reselect";
import WithSpinner from "../WithSpinner/withSpinner";
import { selectIsCollectionFetching,selectShopSectionsLoaded } from "../../Redux/Shop/shopSelector";
import { FetchCollectionStartAsync } from "../../Redux/Shop/shopAction";
import {} from "../CollectionOverview/CollectionOverviewContainer";

const CollectionOverviewWithSpinner = WithSpinner(CollectionOverview);
const CollectionPageWithSpinner = WithSpinner(Collection);

class Shop extends Component {
  componentDidMount() {
    const { fetchCollectionStartAsync } = this.props;
    fetchCollectionStartAsync();
  }

  render() {
    const { match, isCollectionFetching ,isCollectionLoaded} = this.props;

    return (
      <div>
        <Route
          exact
          path={`${match.path}`}
          render={(props) => (
            <CollectionOverviewWithSpinner
              isLoading={isCollectionFetching}
              {...props}
            />
          )}
        />
        <Route
          path={`${match.path}/:collectionId`}
          render={(props) => (
            <CollectionPageWithSpinner
              isLoading={!isCollectionLoaded}
              {...props}
            />
          )}
        />
      </div>
    );
  }
}
const mapDispatchToProps = (dispatch, ownProps) => {
  return {
    fetchCollectionStartAsync: () => {
      dispatch(FetchCollectionStartAsync());
    },
  };
};
const mapStateToProps = createStructuredSelector({
  isCollectionFetching: selectIsCollectionFetching,
  isCollectionLoaded: selectShopSectionsLoaded
});
export default connect(mapStateToProps, mapDispatchToProps)(Shop);
