import React, { Component } from "react";
import FormInput from "../FormInput/FormInput";
import "./SignIn.scss"
import CustomButton from "../CustomButton/CustomButton";

import {connect} from "react-redux";
import {googleSignInStart, emailSignInStart} from "../../Redux/User/userAction";

class SignIn extends Component {
  state = {
    email: "",
    password: "",
  };
  handleSubmit = (e) => {
    e.preventDefault();
    const {email,password}=this.state;
    const {signInWithEmailSaga}=this.props;
    signInWithEmailSaga(email,password)
   
   
    
  };
  handleChange = (event) => {
    this.setState({ [event.target.name]: event.target.value });
  };
  render() {
    const {signInWithGoogleSaga}=this.props;
    return (
      <div className="signin"> 
        <h1>I already have an account</h1>
        <div className="title">Sign In with your email and password</div>
        <form onSubmit={this.handleSubmit} className="form-signin">
          <FormInput
            type="email"
            handleChange={this.handleChange}
            value={this.state.email}
            name="email"
            required
            label="email"
          />
          
          <FormInput
            type="password"
            handleChange={this.handleChange}
            name="password"
            value={this.state.password}
            label="password"
            required
          />
          <div className="buttons">
            <CustomButton type="submit" name="submit" >SIGN IN</CustomButton>
          <CustomButton type="button" name="submit" onClick={signInWithGoogleSaga} isGoogleSignin>SIGN IN WITH GOOGLE</CustomButton>
          </div>
          
        </form>
      </div>
    );
  }
}
const mapDispatchToProps = (dispatch, ownProps) => ({
 
    signInWithGoogleSaga: () => 
      dispatch(googleSignInStart()),
    signInWithEmailSaga: (email,password) => 
      dispatch(emailSignInStart({email,password}))
  
    })

export default connect(null, mapDispatchToProps)(SignIn);
