import React, { Component } from "react";
import FormInput from "../FormInput/FormInput";
import "./SignIn.scss"
import CustomButton from "../CustomButton/CustomButton";
import  { signInWithGoogle } from "../../Firebase/FirebaseUtil";

class SignIn extends Component {
  state = {
    email: "",
    password: "",
  };
  handleSubmit = (e) => {
    e.preventDefault();
    this.setState({ email: "", password: "" });
  };
  handleChange = (event) => {
    this.setState({ [event.target.name]: event.target.value });
  };
  render() {
    return (
      <div>
        <h1>I already have an account</h1>
        <div className="title">Sign In with your email and password</div>
        <form onSubmit={this.handleSubmit} className="form-signin">
          <FormInput
            type="email"
            handleChange={this.handleChange}
            value={this.state.email}
            name="email"
            required
            label="email"
          />
          
          <FormInput
            type="password"
            handleChange={this.handleChange}
            name="password"
            value={this.state.password}
            label="password"
            required
          />
          
          <CustomButton name="submit" >SIGN IN</CustomButton>
          <CustomButton name="submit" onClick={signInWithGoogle}>SIGN IN WITH GOOGLE</CustomButton>
        </form>
      </div>
    );
  }
}

export default SignIn;
