import actionTypes from "./actionTypes";
const InitialState = {
    currentUser:null,
    error:null
}
const userReducer = (state = InitialState, action) => {
    switch (action.type) {
        case actionTypes.SIGNINSUCCESS:
        case actionTypes.EMAILSIGNINSUCCESS:
            return {
                ...state,
                currentUser:action.payload,
                error:null
            }
        case actionTypes.GOOGLESIGNINFAILURE:
        case actionTypes.EMAILSIGNINFAILURE:
            return {
                ...state,
                error:action.payload
            }
       
        default:
            return state
    }
}
export default userReducer;
