import React from "react";
import "./CheckoutItem.scss";
import {ClearItemFromCart} from "../../Redux/Cart/cartAction";
import {connect} from "react-redux";
import {RemoveOne,AddItem} from "../../Redux/Cart/cartAction";

const Checkout = ({ cartItems,clearItem,removeOne }) => {
    const { imageUrl, name, price, quantity }=cartItems
    return(
  <div className="checkout-item">
    <div className="image-container">
      <img src={imageUrl} alt="item" />
    </div>
    <span className="name">{name}</span>
    <span className="quantity">
       <span className="remove">&#10094;</span> {quantity}<span className="remove">&#10095;</span></span>
    <span className="price">{price}</span>
    <span className="remove-button" onClick={()=>clearItem(cartItems)}>&#10006;</span>
  </div>
    )};
const mapDispatchToProps = (dispatch, ownProps) =>({
    
        clearItem: (item) => 
            dispatch(ClearItemFromCart(item))
        ,
        removeOne:(item)=>
            dispatch(RemoveOne(item)),
        addItem:(item)=>dispatch
          
        
    
})

export default connect(null, mapDispatchToProps)(Checkout)
