import {takeEvery,call} from "redux-saga/effects";
import  { actionsTypes } from "../Shop/actionTypes";
import {firestore,convertCollectionSnapshotToMap} from "../../Firebase/FirebaseUtil";
import {FetchCollectionSuccess,FetchCollectionFailure} from "./shopAction";

export function* fetchCollectionAsync(){
    yield console.log("i am fired");

    const collectionRef = firestore.collection("collections");
    const snapshot= yield collectionRef.get();
    const collectionsMap=yield call(convertCollectionSnapshotToMapsnapshot);

    // collectionRef
    //   .get()
    //   .then((snapshot) => {
    //     const collectionMap = convertCollectionSnapshotToMap(snapshot);
    //     dispatch(FetchCollectionSuccess(collectionMap));
    //   })
    //   .catch((error) => dispatch(FetchCollectionFailure(error.message)));
}
export function* fetchCollectionStart(){
    yield takeEvery(actionsTypes.FETCHCOLLECTIONSSTARTS,fetchCollectionAsync)
}
