import React from "react";
import "./CartItem.scss";

const CartItem = ({ item: { imageUrl, name, price } }) => (
  <div className="cart-item">
    <img src={imageUrl} alt="item" />
    <div className="item-details">
      <span className="name">{name}</span>
      <div className="price">
        {quantity}x{price}
      </div>
    </div>
  </div>
);
export default Caty
