import React, { Component } from "react";
import FormInput from "../FormInput/FormInput";
import "./SignIn.scss"
import CustomButton from "../CustomButton/CustomButton";
import  { auth,signInWithGoogle } from "../../Firebase/FirebaseUtil";

class SignIn extends Component {
  state = {
    email: "",
    password: "",
  };
  handleSubmit = (e) => {
    const {email,password}=this.state;
    e.preventDefault();
    try{
      auth.signInWithEmailAndPassword(email,password);
      this.setState({ email: "", password: "" });
    }catch(error){
      
    }
    
  };
  handleChange = (event) => {
    this.setState({ [event.target.name]: event.target.value });
  };
  render() {
    return (
      <div className="signin"> 
        <h1>I already have an account</h1>
        <div className="title">Sign In with your email and password</div>
        <form onSubmit={this.handleSubmit} className="form-signin">
          <FormInput
            type="email"
            handleChange={this.handleChange}
            value={this.state.email}
            name="email"
            required
            label="email"
          />
          
          <FormInput
            type="password"
            handleChange={this.handleChange}
            name="password"
            value={this.state.password}
            label="password"
            required
          />
          <div className="buttons">
            <CustomButton name="submit" >SIGN IN</CustomButton>
          <CustomButton name="submit" onClick={signInWithGoogle} isGoogleSignin>SIGN IN WITH GOOGLE</CustomButton>
          </div>
          
        </form>
      </div>
    );
  }
}

export default SignIn;
