import {actionTypes} from "./actionTypes";
const InitialState = {
    currentUser:null
}
const userReducer = (state = InitialState, action) => {
    switch (action.type) {
        case actionTypes.SETCURRENTUSER:
            return {
                ...state,
                currentUser:action.payload
            }
        case ACTION_TYPE_2:
            return state
        default:
            return state
    }
}
